import {createUseStyles} from 'react-jss';
import { Container, Row, Col } from "reactstrap";
import copy from '../copy.json';
import { useForm, ValidationError } from '@formspree/react';

const useStyles = createUseStyles({
  info: {
    "align-self": "flex-end"
  },
  colKey: {
    textAlign: 'left'
  },
  colValue: {
    textAlign: 'left'
  }
});

function ContactForm() {
  const [state, handleSubmit] = useForm("xvodwvwz");
  if (state.succeeded) {
      return <p>Thanks for joining!</p>;
  }
  return (
      <form onSubmit={handleSubmit}>
      <label htmlFor="email">
        Email Address
      </label>
      <input
        id="email"
        type="email" 
        name="email"
      />
      <ValidationError 
        prefix="Email" 
        field="email"
        errors={state.errors}
      />
      <textarea
        id="message"
        name="message"
      />
      <ValidationError 
        prefix="Message" 
        field="message"
        errors={state.errors}
      />
      <button type="submit" disabled={state.submitting}>
        Submit
      </button>
    </form>
  );
}

function Contact() {
  const classes = useStyles();
  return(
    <div>
      <Container>
        <Row>
          <Col lg={{size: 3, offset: 1}} className={classes.info}>
            <Row>
              <Col className={classes.colKey}>Email: </Col>
              <Col className={classes.colValue}>{copy.Contact.Email}</Col>
            </Row>
            <Row>
              <Col className={classes.colKey}>Phone: </Col>
              <Col className={classes.colValue}>{copy.Contact.Phone}</Col>
            </Row>
            <Row>
              <Col className={classes.colKey}>Address: </Col>
              <Col className={classes.colValue}>{copy.Contact.Address}</Col>
            </Row>
          </Col>
          <Col lg="7">
            <h1>Inquiry form goes here</h1>
            <ContactForm />
            line one <br></br>
            line two <br></br>
            line three <br></br>
            line four <br></br>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default Contact;
