import React, { Component } from 'react';
import {createUseStyles} from 'react-jss';
import {
  Card,
  CardImg,
  CardText,
  CardBody,
  CardTitle,
  // CardSubtitle,
  Container,
  Row,
  Col
} from 'reactstrap';
import { isConstructorTypeNode } from 'typescript';


const useStyles = createUseStyles({
  main: {
    paddingTop: 90
  },
  cardCol: {
    paddingBottom: "2em"
  },
  card: {
    height:"100%",
  },
  services: {
    textAlign: 'left'
  },
  statement: {
    textAlign: 'left'
  },
  colSpace: {
    paddingTop: '1.5em'
  }
});

function makeCards(arr){
  if(arr){
    return( arr.map((d) => {
      console.log(d.item_data)
      return(
        <Col lg="3" md="6">
          <Card>
            <CardBody>
              <CardTitle tag="h3">{d.item_data.name}</CardTitle>
              <CardText>
                {d.item_data.description}
                < br />
                {/* {d.item_data.variations.item_variation_data.price_money.amount} */}
              </CardText>
            </CardBody>
          </Card>
        </Col>
      );
    })
  )
}};


// fetching the GET route from the Express server which matches the GET route from server.js
const callBackendAPI = async () => {
  const response = await fetch('/express_backend');
  const body = await response.json();

  if (response.status !== 200) {
    throw Error(body.message) 
  }
  return body;
};

function componentDidMount() {
  this.callBackendAPI()
    // this one works importing the square data
    .then(res => this.setState({ data: res }))

    //this one works logging the square data
    // .then(res => console.log(res))

    .catch(err => console.log(err));
}

var state = {
  data: null
};

class Order extends Component {
// function Order() {


  // classes = useStyles()
  //main part of the app
  render() {
    return (
      <div>
        <br /><br /><br /><br /><br />
        From the catalog API:
        <Container>
          <Row>
            {makeCards(this.state.data)}
          </Row>
        </Container>
      </div>
    );
  }
}

export default Order;
