import {Component, useState} from 'react';
import logo from '../logo.svg';
import {createUseStyles} from 'react-jss';
import {
  Collapse,
  Nav,
  Navbar,
  NavbarBrand,
  NavbarToggler,
  NavItem,
  NavLink
} from 'reactstrap';

const useStyles = createUseStyles({
  main: {
    // backgroundColor: '#535D8D',
    // display: 'flex'
    "margin-right": 10
  },
  toggle: {
    "margin-right": 10
  },
  logo: {
    "margin-left": 10
  },
  navbar: {
    "justify-content": "flex-end",
    "margin-right": 10
  },
  header: {
    position: "fixed",
    top: 0,
    right: 0,
    left: 0,
    height: "auto",
    padding: "15px 0px",
    zIndex: 1
  }
});

const links = [
  { href: "/", text: 'Home' },
  { href: "/design", text: 'Design' },
  { href: '/manufacturing', text: 'Manufacturing' },
  { href: '/contact', text: 'Contact' }
];

const createNavItem = ({ href, text, className }) => (
  <NavItem>
    <NavLink href={href} className={className}>{text}</NavLink>
  </NavItem>
);

function Header() {
  const classes = useStyles();
  const [isOpen, setIsOpen] = useState(false);
  const toggle = () => {
    setIsOpen(!isOpen);
  }

  return (
    <Navbar color='light' light expand="md" className={classes.header}>>
    {/* <Navbar className={classes.main} expand="md"> */}
      <NavbarBrand href="/" className={classes.logo}>
        <img src={logo} alt="logo" height="30"/>
      </NavbarBrand>
      <NavbarToggler onClick={toggle} className={classes.toggle}/>
      <Collapse isOpen={isOpen} navbar className={classes.navbar}>
        <Nav navbar>
          {links.map(createNavItem)}
        </Nav>
      </Collapse>
    </Navbar>
  );
}

export default Header;
